function search(nameKey, myArray){
	var element;
	for(var i=0; i < myArray.length; i++){

		if(myArray[i].id === nameKey){
			element = myArray[i];
		}
	}
	return element;
}

function compareScore(id, myArray){
	var score = 0.0;
	score = search(id,myArray).relevance;
	helpPrint(score);
	return score;
}

function updateScore(id, myArray){
	var score = 0.0;
	score = search(id,myArray).relevance + score;
	return score;
}

function compareStrings(str1, str2) {
	if(str1 === str2){
		return true;
	} else {
		return false;
	}
}

function getTime() {
	var d = new Date();
	var n = d.getTime();
	return n;
}

function compareTime(oldTime, wait) {
	var d = new Date();
	var t = d.getTime();
	var waitTime = oldTime + wait;
	if (t > waitTime){
		print("wait time "+wait+" passed");
		return true;
	} else {
		return false;
	}
}
